$(document).ready(function(){
   $("#but").click(function(){
      var move = $("#move").val();
      var first = $("#one").val() * 1;
      var second = $("#two").val() * 1;
      var total;
         if (move == '+'){
         	total = first + second;
         }
         else if (move == '-'){
         	total = first - second;
         }
         else if (move == '/'){
         	total = first / second;
         }
         else if (move == '*'){
         	total = first * second;
         }
         $("#total").val(total);
   });
});